import React, { useState } from 'react';
import { Form, Input, Button, message } from 'antd';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Signin = () => {
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const onFinish = async (values) => {
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5500/api/auth/signin', values);
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('username', values.username); // Store the username in localStorage
      message.success('Logged in successfully!');
      navigate('/chatroom');
    } catch (error) {
      message.error('Error logging in');
    }
    setLoading(false);
  };


  return (
    <Form name="signin" onFinish={onFinish}>
      <Form.Item name="username" rules={[{ required: true, message: 'Please input your username!' }]}>
        <Input placeholder="Username" />
      </Form.Item>
      <Form.Item name="password" rules={[{ required: true, message: 'Please input your password!' }]}>
        <Input.Password placeholder="Password" />
      </Form.Item>
      <Form.Item>
        <Button type="primary" htmlType="submit" loading={loading}>Signin</Button>
      </Form.Item>
    </Form>
  );
};

export default Signin;
