import React, { useState, useEffect, useRef } from 'react';
import { Card, List, Input, Button, Layout, Row, Col } from 'antd';
import io from 'socket.io-client';
import './ChatRoom.css';

const { Content } = Layout;

const socket = io('http://localhost:5500');

const ChatRoom = () => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [isGroupChat, setIsGroupChat] = useState(false);
  const chatContainerRef = useRef(null);

  useEffect(() => {
    const username = localStorage.getItem('username');
    if (username) {
      socket.emit('user connected', username);
    }

    socket.emit('get users');
    socket.on('users list', (usersList) => {
      setUsers(usersList.filter(user => user.username !== username)); // Filter out the signed-in user
    });

    socket.on('user status', (userStatus) => {
      setUsers((prevUsers) =>
        prevUsers.map(user =>
          user.username === userStatus.username ? { ...user, status: userStatus.status } : user
        )
      );
    });

    socket.on('chat history', (history) => {
      if (selectedUser && !isGroupChat) {
        localStorage.setItem(`chat_history_${selectedUser}`, JSON.stringify(history));
        setMessages(history);
      }
    });

    socket.on('group chat history', (history) => {
      if (isGroupChat) {
        localStorage.setItem('chat_history_group', JSON.stringify(history));
        setMessages(history);
      }
    });

    // Handle personal chat messages
    socket.on('personal chat message', (msg) => {
      if (!isGroupChat && (msg.to === selectedUser || msg.username === selectedUser || msg.username === localStorage.getItem('username'))) {
        setMessages((prevMessages) => {
          const updatedMessages = [...prevMessages, msg];
          localStorage.setItem(`chat_history_${selectedUser}`, JSON.stringify(updatedMessages));
          return updatedMessages;
        });
      }
    });


    // Handle group chat messages
    socket.on('group chat message', (msg) => {
      if (isGroupChat) {
        setMessages((prevMessages) => {
          const updatedMessages = [...prevMessages, msg];
          localStorage.setItem('chat_history_group', JSON.stringify(updatedMessages));
          return updatedMessages;
        });
      }
    });

    return () => {
      socket.off('users list');
      socket.off('user status');
      socket.off('chat history');
      socket.off('group chat history');
      socket.off('personal chat message');
      socket.off('group chat message');
    };
  }, [selectedUser, isGroupChat]);

  useEffect(() => {
    if (isGroupChat) {
      const savedHistory = localStorage.getItem('chat_history_group');
      if (savedHistory) {
        setMessages(JSON.parse(savedHistory));
      } else {
        socket.emit('get group chat history');
      }
    } else if (selectedUser) {
      const savedHistory = localStorage.getItem(`chat_history_${selectedUser}`);
      if (savedHistory) {
        setMessages(JSON.parse(savedHistory));
      } else {
        socket.emit('get chat history', selectedUser);
      }
    }
  }, [selectedUser, isGroupChat]);

  const sendMessage = () => {
    const username = localStorage.getItem('username');
    if (!username) {
      console.error('Username is not found in localStorage');
      return;
    }

    const msg = { username, text: message, timestamp: new Date() };

    if (isGroupChat) {
      socket.emit('group chat message', msg);
    } else {
      socket.emit('chat message', { ...msg, to: selectedUser });
      // Do not add the message to the local state here
    }
    setMessage('');
  };


  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    window.location.href = '/';
  };

  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <Layout style={{ height: '100vh' }}>
      <Row justify="center" align="middle" style={{ height: '100%', padding: '10px' }}>
        <Col xs={24} sm={22} md={20} lg={18} xl={16}>
          <Card
            className="chatroom-card"
            title="Chat Room"
            bordered={false}
            extra={<Button onClick={logout}>Logout</Button>}
          >
            <Row gutter={[16, 16]}>
              <Col xs={24} md={8} className="chatroom-sider">
                <List
                  dataSource={['Group Chat', ...users.map(user => user.username)]}
                  renderItem={(user) => (
                    <List.Item
                      style={{ cursor: 'pointer' }}
                      onClick={() => {
                        if (user === 'Group Chat') {
                          setSelectedUser(null);
                          setIsGroupChat(true);
                        } else {
                          setSelectedUser(user);
                          setIsGroupChat(false);
                        }
                      }}
                    >
                      <div style={{ display: 'flex', alignItems: 'center' }}>
                        <div
                          className={users.find(u => u.username === user)?.status === 'online' ? 'user-badge' : 'user-badge-offline'}
                        />
                        <strong style={{ marginLeft: '8px' }}>{user}</strong>
                      </div>
                    </List.Item>
                  )}
                />
              </Col>
              <Col xs={24} md={16}>
                <Content style={{ padding: '10px' }}>
                  {isGroupChat || selectedUser ? (
                    <Card title={isGroupChat ? "Group Chat" : `Chat with ${selectedUser}`} bordered={false}>
                      <div ref={chatContainerRef} id="chat-container" className="chat-container" style={{ height: '300px', overflowY: 'auto' }}>
                        <List
                          dataSource={messages}
                          renderItem={(item) => (
                            <List.Item>
                              <strong>{item.username}:</strong> {item.text}{' '}
                              <em>{new Date(item.timestamp).toLocaleTimeString()}</em>
                            </List.Item>
                          )}
                        />
                      </div>
                      <Input
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        onPressEnter={sendMessage}
                        placeholder="Type a message..."
                        style={{ marginBottom: '10px' }}
                      />
                      <Button type="primary" onClick={sendMessage}>
                        Send
                      </Button>
                    </Card>
                  ) : (
                    <div>Please select a user to start chatting</div>
                  )}
                </Content>
              </Col>
            </Row>
          </Card>
        </Col>
      </Row>
    </Layout>
  );
};

export default ChatRoom;
