import React, { useState, useEffect, useRef } from 'react';
import { Card, List, Input, Button, Layout, Row, Col } from 'antd';
import io from 'socket.io-client';
import moment from 'moment';
// import './ChatRoom.css';

const { Content } = Layout;

const socket = io('http://localhost:5500');

const ChatRoom = () => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [isGroupChat, setIsGroupChat] = useState(false);
  const chatContainerRef = useRef(null);

  useEffect(() => {
    const username = localStorage.getItem('username');
    if (username) {
      socket.emit('user connected', username);
    }

    socket.emit('get users');
    socket.on('users list', (usersList) => {
      setUsers(usersList.filter(user => user.username !== username)); // Filter out the signed-in user
    });

    socket.on('user status', (userStatus) => {
      setUsers((prevUsers) =>
        prevUsers.map(user =>
          user.username === userStatus.username ? { ...user, status: userStatus.status } : user
        )
      );
    });

    socket.on('chat history', (history) => {
      if (selectedUser && !isGroupChat) {
        localStorage.setItem(`chat_history_${selectedUser}`, JSON.stringify(history));
        setMessages(history);
      }
    });

    socket.on('group chat history', (history) => {
      if (isGroupChat) {
        localStorage.setItem('chat_history_group', JSON.stringify(history));
        setMessages(history);
      }
    });

    // Handle personal chat messages
    socket.on('personal chat message', (msg) => {
      if (!isGroupChat && (msg.to === selectedUser || msg.username === selectedUser || msg.username === localStorage.getItem('username'))) {
        setMessages((prevMessages) => {
          const updatedMessages = [...prevMessages, msg];
          localStorage.setItem(`chat_history_${selectedUser}`, JSON.stringify(updatedMessages));
          return updatedMessages;
        });
      }
    });


    // Handle group chat messages
    socket.on('group chat message', (msg) => {
      if (isGroupChat) {
        setMessages((prevMessages) => {
          const updatedMessages = [...prevMessages, msg];
          localStorage.setItem('chat_history_group', JSON.stringify(updatedMessages));
          return updatedMessages;
        });
      }
    });

    return () => {
      socket.off('users list');
      socket.off('user status');
      socket.off('chat history');
      socket.off('group chat history');
      socket.off('personal chat message');
      socket.off('group chat message');
    };
  }, [selectedUser, isGroupChat]);

  useEffect(() => {
    if (isGroupChat) {
      const savedHistory = localStorage.getItem('chat_history_group');
      if (savedHistory) {
        setMessages(JSON.parse(savedHistory));
      } else {
        socket.emit('get group chat history');
      }
    } else if (selectedUser) {
      const savedHistory = localStorage.getItem(`chat_history_${selectedUser}`);
      if (savedHistory) {
        setMessages(JSON.parse(savedHistory));
      } else {
        socket.emit('get chat history', selectedUser);
      }
    }
  }, [selectedUser, isGroupChat]);

  const sendMessage = () => {
    const username = localStorage.getItem('username');
    if (!username) {
      console.error('Username is not found in localStorage');
      return;
    }

    const msg = { username, text: message, timestamp: new Date() };

    if (isGroupChat) {
      socket.emit('group chat message', msg);
      setMessages((prevMessages) => [...prevMessages, msg]);
      localStorage.setItem('chat_history_group', JSON.stringify([...messages, msg]));
    } else if (selectedUser) {
      const msgToSend = { ...msg, to: selectedUser };
      socket.emit('personal chat message', msgToSend);
      setMessages((prevMessages) => [...prevMessages, msgToSend]);
      localStorage.setItem(`chat_history_${selectedUser}`, JSON.stringify([...messages, msgToSend]));
    }
    setMessage('');
  };

  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <Layout className="chatroom-layout">
      <Row>
        <Col xs={24} sm={8} md={6}>
          <Card className="chatroom-sider">
            <List
              itemLayout="horizontal"
              dataSource={users}
              renderItem={(user) => (
                <List.Item
                  className={
                    user.username === selectedUser && !isGroupChat ? 'ant-list-item-selected' : ''
                  }
                  onClick={() => {
                    setSelectedUser(user.username);
                    setIsGroupChat(false);
                  }}
                >
                  <List.Item.Meta
                    avatar={<span className={`user-badge ${user.status === 'online' ? 'user-badge-online' : 'user-badge-offline'}`} />}
                    title={user.username}
                  />
                </List.Item>
              )}
            />
            <Button
              type={isGroupChat ? 'primary' : 'default'}
              block
              onClick={() => {
                setSelectedUser(null);
                setIsGroupChat(true);
              }}
              style={{ marginTop: '15px' }}
            >
              Group Chat
            </Button>
          </Card>
        </Col>
        <Col xs={24} sm={16} md={18}>
          <Content className="chatroom-content">
            <Card className="chatroom-card">
              <div ref={chatContainerRef} className="chat-container">
                {messages.map((msg, index) => (
                  <div key={index} className="chat-message">
                    <strong>{msg.username}</strong>: {msg.text} <span>{moment(msg.timestamp).format('HH:mm:ss')}</span>
                  </div>
                ))}
              </div>
              <Input
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onPressEnter={sendMessage}
                placeholder="Type a message..."
              />
              <Button type="primary" onClick={sendMessage}>
                Send
              </Button>
            </Card>
          </Content>
        </Col>
      </Row>
      <footer className="chatroom-footer">© {moment().year()}</footer>
    </Layout>
  );
};

export default ChatRoom;
