import React, { useState } from 'react';
import { Form, Input, Button, message, Card } from 'antd';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Signin.css'; // Ensure this CSS file includes the necessary styles

const Signin = () => {
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const onFinish = async (values) => {
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5500/api/auth/signin', values);
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('username', values.username); // Store the username in localStorage
      message.success('Logged in successfully!');
      navigate('/chatroom');
    } catch (error) {
      message.error('Error logging in');
    }
    setLoading(false);
  };

  return (
    <div className="signin-container">
      <video autoPlay muted loop className="background-video">
        <source src="/videos/background.mp4" type="video/mp4" />
        Your browser does not support HTML5 video.
      </video>
      <div className="form-overlay">
        <Card
          title="Sign In"
          bordered={false}
          style={{ maxWidth: 400, width: '100%', margin: '0 auto', borderRadius: '8px' }}
        >
          <Form name="signin" onFinish={onFinish}>
            <Form.Item
              name="username"
              rules={[{ required: true, message: 'Please input your username!' }]}
            >
              <Input placeholder="Username" />
            </Form.Item>
            <Form.Item
              name="password"
              rules={[{ required: true, message: 'Please input your password!' }]}
            >
              <Input.Password placeholder="Password" />
            </Form.Item>
            <Form.Item>
              <Button type="primary" htmlType="submit" loading={loading} block>
                Sign In
              </Button>
            </Form.Item>
          </Form>
        </Card>
      </div>
    </div>
  );
};

export default Signin;
