import React, { useState, useEffect } from 'react';
import { Card, List, Input, Button, Layout } from 'antd';
import io from 'socket.io-client';
import './ChatRoom.css'; // Import custom CSS

const { Sider, Content } = Layout;

const socket = io('http://localhost:5500');

const ChatRoom = () => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    socket.emit('get users');
    socket.on('users list', (usersList) => {
      setUsers(usersList);
    });

    if (selectedUser) {
      socket.emit('get chat history', selectedUser);
    }

    socket.on('chat history', (history) => {
      setMessages(history);
    });

    socket.on('chat message', (msg) => {
      setMessages((prevMessages) => [...prevMessages, msg]);
    });

    return () => {
      socket.off('users list');
      socket.off('chat history');
      socket.off('chat message');
    };
  }, [selectedUser]);

  const sendMessage = () => {
    const username = localStorage.getItem('username');
    if (!username) {
      console.error('Username is not found in localStorage');
      return;
    }

    console.log('Sending message from user:', username);
    socket.emit('chat message', { username, text: message, to: selectedUser });
    setMessage('');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    window.location.href = '/'; // Redirect to the signin page
  };

  useEffect(() => {
    const chatContainer = document.getElementById('chat-container');
    if (chatContainer) {
      chatContainer.scrollTop = chatContainer.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="chatroom-container">
      <Card className="chatroom-card">
        <Button type="primary" danger style={{ float: 'right' }} onClick={handleLogout}>
          Logout
        </Button>
        <Layout style={{ height: 'calc(100vh - 70px)' }}>
          <Sider width={300} style={{ background: '#fff', padding: '20px' }}>
            <List
              dataSource={users}
              renderItem={(user) => (
                <List.Item
                  style={{ cursor: 'pointer' }}
                  onClick={() => setSelectedUser(user.username)}
                >
                  <strong>{user.username}</strong>
                </List.Item>
              )}
            />
          </Sider>
          <Content style={{ padding: '20px' }}>
            {selectedUser ? (
              <Card title={`Chat with ${selectedUser}`} bordered={false}>
                <List
                  id="chat-container"
                  dataSource={messages}
                  renderItem={(item) => (
                    <List.Item>
                      <strong>{item.username}:</strong> {item.text}{' '}
                      <em>{new Date(item.timestamp).toLocaleTimeString()}</em>
                    </List.Item>
                  )}
                  style={{ marginBottom: '20px', maxHeight: '400px', overflowY: 'auto' }}
                />
                <Input
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  onPressEnter={sendMessage}
                  placeholder="Type a message..."
                  style={{ marginBottom: '10px' }}
                />
                <Button type="primary" onClick={sendMessage}>
                  Send
                </Button>
              </Card>
            ) : (
              <div>Please select a user to start chatting</div>
            )}
          </Content>
        </Layout>
      </Card>
    </div>
  );
};

export default ChatRoom;
